CYRILLIC_MAP = {
    "–∞":1,"–±":2,"–≤":3,"–≥":4,"–¥":5,"–µ":6,"—ë":7,"–∂":8,"–∑":9,
    "–∏":1,"–π":2,"–∫":3,"–ª":4,"–º":5,"–Ω":6,"–æ":7,"–ø":8,"—Ä":9,
    "—Å":1,"—Ç":2,"—É":3,"—Ñ":4,"—Ö":5,"—Ü":6,"—á":7,"—à":8,"—â":9,
    "—ä":1,"—ã":2,"—å":3,"—ç":4,"—é":5,"—è":6,
}

LATIN_MAP = {
    "a":1,"b":2,"c":3,"d":4,"e":5,"f":6,"g":7,"h":8,"i":9,
    "j":1,"k":2,"l":3,"m":4,"n":5,"o":6,"p":7,"q":8,"r":9,
    "s":1,"t":2,"u":3,"v":4,"w":5,"x":6,"y":7,"z":8,
}

VOWELS_CYR = set("–∞–µ—ë–∏–æ—É—ã—ç—é—è")
VOWELS_LAT = set("aeiou")
MASTER_NUMBERS = {11, 22, 33}


class NumerologyEngine:

    @staticmethod
    def _reduce(n: int, keep_master: bool = True) -> int:
        while n > 9:
            if keep_master and n in MASTER_NUMBERS:
                return n
            n = sum(int(d) for d in str(n))
        return n

    @staticmethod
    def _char_to_number(char: str) -> int:
        char = char.lower()
        return CYRILLIC_MAP.get(char, LATIN_MAP.get(char, 0))

    @staticmethod
    def _is_vowel(char: str) -> bool:
        char = char.lower()
        return char in VOWELS_CYR or char in VOWELS_LAT

    def calculate_life_path(self, birth_date: date) -> int:
        day = self._reduce(birth_date.day)
        month = self._reduce(birth_date.month)
        year = self._reduce(sum(int(d) for d in str(birth_date.year)))
        return self._reduce(day + month + year)

    def calculate_soul_number(self, name: str) -> int:
        total = sum(self._char_to_number(c) for c in name if self._is_vowel(c))
        return self._reduce(total)

    def calculate_personality_number(self, name: str) -> int:
        total = sum(self._char_to_number(c) for c in name if c.isalpha() and not self._is_vowel(c))
        return self._reduce(total)

    def calculate_destiny_number(self, full_name: str, birth_date: date) -> int:
        total = sum(self._char_to_number(c) for c in full_name if c.isalpha())
        return self._reduce(total)

    def calculate_personal_year(self, birth_date: date, current_year: int | None = None) -> int:
        if current_year is None:
            current_year = date.today().year
        day = self._reduce(birth_date.day)
        month = self._reduce(birth_date.month)
        year = self._reduce(sum(int(d) for d in str(current_year)))
        return self._reduce(day + month + year)

    def full_report(self, name: str, birth_date: date) -> dict[str, int]:
        return {
            "life_path": self.calculate_life_path(birth_date),
            "soul": self.calculate_soul_number(name),
            "personality": self.calculate_personality_number(name),
            "destiny": self.calculate_destiny_number(name, birth_date),
            "personal_year": self.calculate_personal_year(birth_date),
        }

